<script>
    import { mapGetters } from 'vuex';
    import Velocity from 'velocity-animate';

    export default {
        computed: {
            ...mapGetters({
                isAuthed: 'auth/hasToken'
            })
        },

        methods: {
            loginLeave(el, done) {
                Velocity(el, {
                    translateX: '-30%',
                    opacity: 0
                },
                {
                    duration: 350,
                    complete: () => {
                        this.$router.push({
                            name: 'home'
                        });
                    }
                });
            }
        }
    }
</script>
